var Fruck = -D*y;
var Fr0 = -Freib*Math.sign(v);
var Fr1 = -Fspm*v;
var Fr2 = -Fsspmm*v*Math.abs(v);
var Frs = resonanz(t);
var F = Fruck+ Fr0 + Fr1 + Fr2 + Frs;
var a = F/m;
v = v + a*dt;
y = y + v*dt;
t = t + dt;

startprg:
var t = 0;
var dt = 0.02;
var y = 0;
var v = 0;
var D = 2;
var m = 1;

var fRes = prompt("fRes",0.225193242);
var FresMax = 0.1;

var Freib = 0.001;// Gleitreibung
var Fspm = 0.2;// Visoksitätsreibung
var Fsspmm = 0.2;// Luftstoßreibung

function resonanz(t) {//returns Fres
   return Math.sin(t*2*Math.PI*fRes)*FresMax;
}
